#[cfg(test)]
mod tests {
    use bencode::ByteBuf;

    fn debug_hex_bencode(name: &str, data: &[u8]) {
        println!("{}", name);
        let data = hex::decode(data).unwrap();
        println!(
            "{:#?}",
            bencode::dyn_from_bytes::<ByteBuf>(data.as_slice()).unwrap()
        );
    }

    #[tokio::test]
    async fn it_works() {
        debug_hex_bencode("req: find_node", b"64313a6164323a696432303abd7b477cfbcd10f30b705da20201e7101d8df155363a74617267657432303abd7b477cfbcd10f30b705da20201e7101d8df15565313a71393a66696e645f6e6f6465313a74323a0005313a79313a7165");
        debug_hex_bencode("req: get_peers", b"64313a6164323a696432303abd7b477cfbcd10f30b705da20201e7101d8df155393a696e666f5f6861736832303acab507494d02ebb1178b38f2e9d7be299c86b86265313a71393a6765745f7065657273313a74323a0006313a79313a7165");
        debug_hex_bencode("resp from the requesting node", b"");
        debug_hex_bencode("resp from some random IP", b"");
        debug_hex_bencode("another resp from some random IP", b"");
        debug_hex_bencode("req to another node", b"64313a6164323a696432303abd7b477cfbcd10f30b705da20201e7101d8df155393a696e666f5f6861736832303acab507494d02ebb1178b38f2e9d7be299c86b86265313a71393a6765745f7065657273313a74323a0007313a79313a7165");
    }
}
